import React, { useState } from 'react'
import validator from 'validator'
import './preBook.css'
export default function PreBook() {

  const initialValues = { Name: "", email: "", phone: "" };
  const [formValues, setFormValues] = useState(initialValues);
  const [formErrors, setFormErrors] = useState({});
  const [isSubmit, setIsSubmit] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormValues({ ...formValues, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setFormErrors(validate(formValues));
    setIsSubmit(true);
  };

  useEffect(() => {
    console.log(formErrors);
    if (Object.keys(formErrors).length === 0 && isSubmit) {
      console.log(formValues);
    }
  }, [formErrors]);
  const validate = (values) => {
    const errors = {};
    const regex = /^[^\s@]+@[^\s@]+\.[^\s@]{2,}$/i;
    if (!values.Name) {
      errors.Name = "Name is required!";
    }
    if (!values.email) {
      errors.email = "Email is required!";
    } else if (!regex.test(values.email)) {
      errors.email = "This is not a valid email format!";
    }
    if (!values.phone) {
      errors.phone = "phone is required";
    } else if (values.phone.length < 0) {
      errors.phone = "phone must be more than 0 characters";
    } else if (values.phone.length > 10) {
      errors.phone = "phone cannot exceed more than 10 characters";
    }
    return errors;
  };

  return (
    <div className="container-prebook">
      {Object.keys(formErrors).length === 0 && isSubmit ? (
        <div className="ui message success">Thank you for your submission, our team will reach out to you soon</div>
      ) : (
        <pre></pre>
      )}
 <h1 className="heading-prebook">Pre-Book our EV’s Now</h1>
      <form onSubmit={handleSubmit}>

        <div className="ui divider"></div>
        <div className="ui form">
          <p className="pre-heading">Fill out the following details to pre book your vehicle</p>
          <div className="field">
            <label>Name</label>
            <br/>
            <input className="input-pre"
              type="text"
              name="Name"
              placeholder="Enter Full Name"
              value={formValues.Name}
              onChange={handleChange}
            />
          </div>
          <p>{formErrors.Name}</p>
          <div className="field">
            <label>Email</label>
            <br/>
            <input className="input-pre"
              type="text"
              name="email"
              placeholder="Email"
              value={formValues.email}
              onChange={handleChange}
            />
          </div>
          <p>{formErrors.email}</p>
          <div className="field">
            <label>Phone</label>
            <br/>
            <input className="input-pre"
              type="phone"
              name="phone"
              placeholder="Enter Phone Number"
              value={formValues.phone}
              onChange={handleChange}
            />
          </div>
          <p>{formErrors.phone}</p>
          <div className="btn-btn">
          <button className="pre-btn" type="button" >Submit</button>
          </div>

        </div>
      </form>
    </div>
  );
}
